function handlePopup(a){var b=jQuery(a);b.click(function(){var a=575,b=400,c=(jQuery(window).height()-b)/2,d=(jQuery(window).width()-a)/2,e=this.href,f="width="+a+",height="+b+",top="+c+",left="+d;window.open(e,"share-popup",f);return false})}jQuery(function(){handlePopup("#custom-tweet-button");handlePopup("#custom-facebook-share-button");handlePopup("#custom-google-share-button");if(jQuery("#custom-tweet-button").length){currentUrl=window.location.href;jQuery.ajax({dataType:"jsonp",url:"https://urls.api.twitter.com/1/urls/count.json?url="+currentUrl,success:function(a){if("count"in a&&!isNaN(a.count)){jQuery("#custom-tweet-button").after('<a id="tweet-count" href="https://twitter.com/search?q='+encodeURIComponent(currentUrl)+'" target="_blank">'+a.count+"</a>")}}})}})
